create table "Messages"
(
    "MessageId"  uuid      not null
        constraint "PK_Messages"
            primary key,
    "Uid"        uuid      not null,
    "Username"   text      not null,
    "Avatar"     text,
    "Content"    text      not null,
    "Type"       text      not null,
    "CreatedAt"  timestamp not null,
    "InboxRefId" uuid      not null,
    "InboxId"    uuid
        constraint "FK_Messages_Inboxes_InboxId"
            references "Inboxes"
            on delete restrict
);

alter table "Messages"
    owner to postgres;

create index "IX_Messages_InboxId"
    on "Messages" ("InboxId");

